		  		   and a.activi_end_time = #{activiEndTime} 
		  		   and a.member_id = #{memberId} 

		  		  	and
		  		  	a.team_id in 

				            #{item}       





                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from activity

