mapper.update:
update activity
`act_intro` = #{actIntro}, `act_title` = #{actTitle}, `act_content` = #{actContent}, `act_price` = #{actPrice}, `status` = #{status}, `examine_status` = #{examineStatus}, `act_title_img` = #{actTitleImg}, `act_type_id` = #{actTypeId}, `start_time` = #{startTime}, `end_time` = #{endTime}, `create_time` = #{createTime}, `ticket_count` = #{ticketCount}, `act_type` = #{actType}, `restrictions` = #{restrictions}, `number_of_shares` = #{numberOfShares}, `number_of_collection` = #{numberOfCollection}, `number_of_previews` = #{numberOfPreviews}, `number_of_applicants` = #{numberOfApplicants}, `applicants_num_max` = #{applicantsNumMax}, `reason_for_refusal` = #{reasonForRefusal}, activi_start_time = #{activiStartTime}, activi_end_time = #{activiEndTime}, team_id = #{teamId}
where id = #{id}

update activity
`number_of_shares` = number_of_shares+1, `number_of_collection` = number_of_collection+1, `number_of_previews` = number_of_previews+1, `number_of_applicants` = number_of_applicants+1
where id = #{id}

update activity
`number_of_shares` = number_of_shares-1, `number_of_collection` = number_of_collection-1, `number_of_previews` = number_of_previews-1, `number_of_applicants` = number_of_applicants-1
where id = #{id}
