mapper.select:
		select a.id,a.zyz_id,a.act_id,v.volunteer_name, ac.act_title,
		a.create_time,
		DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s') createTimeStr,
		a.examine,a.status,a.reason_for_refusal from act_apply a left join volunteer v on a.zyz_id = v.id left join activity ac on a.act_id = ac.id

		  		   and a.id = #{id} 
		  		   and a.zyz_id = #{zyzId} 
		  		   and a.act_id = #{actId} 
		  		   and a.create_time = #{createTime} 
		  		   and a.examine = #{examine} 
		  		   and a.status = #{status} 
		  		   and a.reason_for_refusal = #{reasonForRefusal} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from act_apply

  		   and id = #{id} 
