mapper.select:
		select h.id,h.member_id,u.name,h.title,h.help_type_id,t.help_title,h.content,h.status,h.del_time,h.update_time,h.public_time,h.creat_time,h.del,h.level,h.district_id from help_center h left join help_type_title t on h.help_type_id = t.id left join sys_user u on h.member_id = u.user_id

		  		   and h.id = #{id} 
		  		   and h.member_id = #{memberId} 
		  		   and h.title = #{title} 
		  		   and h.help_type_id = #{helpTypeId} 
		  		   and h.content = #{content} 
		  		   and h.status = #{status} 
		  		   and h.del_time = #{delTime} 
		  		   and h.update_time = #{updateTime} 
		  		   and h.public_time = #{publicTime} 
		  		   and h.creat_time = #{creatTime} 
		  		   and h.del = #{del} 
		  		   and h.level = #{level} 
		  		   and h.district_id = #{districtId} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from help_center

  		   and id = #{id} 
  		   and member_id = #{memberId} 
  		   and title = #{title} 
  		   and help_type_id = #{helpTypeId} 
  		   and content = #{content} 
