mapper.select:
		select m.`id`,`name`,`sex`,`head_img`,`age`,`address`,`telephone`,`login_no`,`password`,`team_id`,
		`account`,`red_envelope_income`,`platform_integral`,`county`,`city`,`regionCode`,`province`,
		`is_identification`, t.team_name team_name from member m
		left join team t on m.team_id = t.id
          
		  		   and id = #{id} 
		  		   and name like  CONCAT('%',CONCAT(#{name},'%')) 
		  		   and sex = #{sex} 
		  		   and head_img = #{headImg} 
		  		   and age = #{age} 
		  		   and address = #{address} 
		  		   and telephone = #{telephone} 
		  		   and login_no = #{loginNo} 
		  		   and password = #{password} 
		  		   and team_id = #{teamId} 
		  		   and account = #{account} 
		  		   and red_envelope_income = #{redEnvelopeIncome} 
		  		   and platform_integral = #{platformIntegral} 
		  		   and county = #{county} 
		  		   and city = #{city} 
		  		   and regionCode = #{regioncode} 
		  		   and province = #{province} 
		  		   and is_identification = #{isIdentification} 
		  		  
		  		  	and
		  		  	m.team_id in 
		  		   	
				            #{item}       
				     
		  		  
		  		  
		  		  	and
		  		  	m.team_id in 
		  		   	
				            #{item}       
				     
		  		  
		  		
        
            
                order by ${sort} ${order}
            
			
                order by id desc
			
        
		
			limit #{offset}, #{limit}

