mapper.select:
		select `id`,`news_id`,`member_id`,`transpond_type`,`type`,`creat_time`, act_type from new_dynamic
          
		  		   and id = #{id} 
		  		   and news_id = #{newsId} 
		  		   and member_id = #{memberId} 
		  		   and transpond_type = #{transpondType} 
		  		   and type = #{type} 
		  		   and creat_time = #{creatTime} 
		  		    and act_type = #{actType} 
		  		
        
            
                order by ${sort} ${order}
            
			
                order by id desc
			
        
		
			limit #{offset}, #{limit}

select count(*) from new_dynamic
   
  		   and id = #{id} 
  		   and news_id = #{newsId} 
  		   and member_id = #{memberId} 
  		   and transpond_type = #{transpondType} 
