mapper.select:
		select n.is_del,n.is_red_peper,n.is_top,n.type,n.number_of_shares,n.criticism_of_count,n.number_of_likes,n.level,n.reward_max,n.reward_num_of_people,n.reward_count,n.reward_price,n.pyment_end_time,n.pyment_start_time,n.payment_criterion,n.payment_duration,n.payment_top,n.red_peper_id,n.red_peper,n.create_time,n.title_img,n.source,n.public_time,n.status,n.team_id,t.team_name,t.team_title_img,n.introduction,n.content,n.author,n.title,n.id,n.top_region from new_info n left join team t on n.team_id = t.id

		  		   and n.is_del = #{isDel} 
		  		   and n.is_red_peper = #{isRedPeper} 
		  		   and n.is_top = #{isTop} 
		  		   and n.type = #{type} 
		  		   and n.number_of_shares = #{numberOfShares} 
		  		   and n.criticism_of_count = #{criticismOfCount} 
		  		   and n.number_of_likes = #{numberOfLikes} 
		  		   and n.level = #{level} 
		  		   and n.reward_max = #{rewardMax} 
		  		   and n.reward_num_of_people = #{rewardNumOfPeople} 
		  		   and n.reward_count = #{rewardCount} 
		  		   and n.reward_price = #{rewardPrice} 
		  		   and n.pyment_end_time = #{pymentEndTime} 
		  		   and n.pyment_start_time = #{pymentStartTime} 
		  		   and n.payment_criterion = #{paymentCriterion} 
		  		   and n.payment_duration = #{paymentDuration} 
		  		   and n.payment_top = #{paymentTop} 
		  		   and n.red_peper_id = #{redPeperId} 
		  		   and n.red_peper = #{redPeper} 
		  		   and n.title_img = #{titleImg} 
		  		   and n.source = #{source} 
		  		   and n.public_time = #{publicTime} 
		  		   and n.status = #{status} 
		  		   and n.team_id = #{teamId} 
		  		   and n.introduction = #{introduction} 
		  		   and n.content = #{content} 
		  		   and n.author = #{author} 
		  		   and n.title like concat('%',#{title},'%')  
		  		   and n.id = #{id} 
 				   AND n.create_time >= #{startTime}
				  AND n.create_time <= #{endTime}
				  AND n.top_region = #{topRegion}

		         	and left(n.team_id,CHAR_LENGTH(#{ids})) LIKE CONCAT('%',#{ids},'%')  




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

