mapper.update:
update new_info
number_of_shares = number_of_shares+1, criticism_of_count = criticism_of_count+1, number_of_likes = number_of_likes+1, reward_count = reward_count+1
where id = #{newsId}

mapper.select:
		select a.id,n.title, a.top_day, o.price,o.business_time,u.name,a.status from appy_top a LEFT JOIN gyds_order o 
		ON a.orderNumber = o.id
		LEFT JOIN new_info n
		ON a.news_id = n.id
		LEFT JOIN sys_user u
		ON u.user_id = a.user_id

		  a.news_id = #{newsId}



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select `id`,`title`,`author`,`source`,`title_img`,`introduction`,`content`,`number_of_shares`,`criticism_of_count`,`number_of_likes`,`team_id`,`status`,`public_time`,`create_time`,`red_peper`,`red_peper_id`,`is_del`,`is_red_peper`,`apply_top`,`is_top`,`type`,`reward_count`,`reward_price`,`payment_top`,`pyment_end_time`,`pyment_start_time`,`payment_criterion`,`payment_duration`,`level`,`reward_max`,`reward_num_of_people`,`top_region` from new_info

		  		   and team_id = #{teamId} 
		  		   and status = #{status} 
		  		   and public_time = #{publicTime} 
		  		   and create_time = #{createTime} 
		  		   and is_del = #{isDel} 
		  		   and apply_top = #{applyTop} 
		  		   and is_top = #{isTop} 
		  		   and payment_top = #{paymentTop} 
		  		   and pyment_start_time = #{pymentStartTime} 
		  		   and payment_criterion = #{paymentCriterion} 
		  		   and payment_duration = #{paymentDuration} 
		  		   and top_region = #{topRegion} 

		  		   and pyment_end_time <= #{endTime} 




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}
