mapper.select:
		select n.is_del,n.is_red_peper,n.is_top,n.type,n.number_of_shares,n.criticism_of_count,n.number_of_likes,n.level,n.reward_max,n.reward_num_of_people,n.reward_count,n.reward_price,n.pyment_end_time,n.pyment_start_time,n.payment_criterion,n.payment_duration,n.payment_top,n.red_peper_count,n.red_peper,n.create_time,n.title_img,n.source,n.public_time,n.status,n.team_id,t.team_name,n.introduction,n.content,n.author,n.title,n.id from new_info n left join team t on n.team_id = t.id

		  		   and n.is_del = #{isDel} 
		  		   and n.is_red_peper = #{isRedPeper} 
		  		   and n.is_top = #{isTop} 
		  		   and n.type = #{type} 
		  		   and n.number_of_shares = #{numberOfShares} 
		  		   and n.criticism_of_count = #{criticismOfCount} 
		  		   and n.number_of_likes = #{numberOfLikes} 
		  		   and n.level = #{level} 
		  		   and n.reward_max = #{rewardMax} 
		  		   and n.reward_num_of_people = #{rewardNumOfPeople} 
		  		   and n.reward_count = #{rewardCount} 
		  		   and n.reward_price = #{rewardPrice} 
		  		   and n.pyment_end_time = #{pymentEndTime} 
		  		   and n.pyment_start_time = #{pymentStartTime} 
		  		   and n.payment_criterion = #{paymentCriterion} 
		  		   and n.payment_duration = #{paymentDuration} 
		  		   and n.payment_top = #{paymentTop} 
		  		   and n.red_peper_count = #{redPeperCount} 
		  		   and n.red_peper = #{redPeper} 
		  		   and n.create_time = #{createTime} 
		  		   and n.title_img = #{titleImg} 
		  		   and n.source = #{source} 
		  		   and n.public_time = #{publicTime} 
		  		   and n.status = #{status} 
		  		   and n.team_id = #{teamId} 
		  		   and n.introduction = #{introduction} 
		  		   and n.content = #{content} 
		  		   and n.author = #{author} 
		  		   and n.title = #{title} 
		  		   and n.id = #{id} 
 				   AND n.create_time >= #{startTime}
				  AND n.create_time <= #{endTime}



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from new_info

		  		   and is_del = #{isDel} 
		  		   and is_red_peper = #{isRedPeper} 
		  		   and is_top = #{isTop} 
		  		   and type = #{type} 
		  		   and number_of_shares = #{numberOfShares} 
		  		   and criticism_of_count = #{criticismOfCount} 
		  		   and number_of_likes = #{numberOfLikes} 
		  		   and level = #{level} 
		  		   and reward_max = #{rewardMax} 
		  		   and reward_num_of_people = #{rewardNumOfPeople} 
		  		   and reward_count = #{rewardCount} 
		  		   and reward_price = #{rewardPrice} 
		  		   and pyment_end_time = #{pymentEndTime} 
		  		   and pyment_start_time = #{pymentStartTime} 
		  		   and payment_criterion = #{paymentCriterion} 
		  		   and payment_duration = #{paymentDuration} 
		  		   and payment_top = #{paymentTop} 
		  		   and red_peper_count = #{redPeperCount} 
		  		   and red_peper = #{redPeper} 
		  		   and create_time = #{createTime} 
		  		   and title_img = #{titleImg} 
		  		   and source = #{source} 
		  		   and public_time = #{publicTime} 
		  		   and status = #{status} 
		  		   and team_id = #{teamId} 
		  		   and introduction = #{introduction} 
		  		   and content = #{content} 
		  		   and author = #{author} 
		  		   and title = #{title} 
		  		   and id = #{id} 

            		and create_time between #{startTime} and #{endTime}
