mapper.select:
		select p.`id`,p.`name`,`status`,`level`,`audit_status`,`region_level`,`member_id`, proxy_addr,
		`audit_result`,`audit_opinion`,
		m.age,m.city,m.county,m.`name` memberName,m.province,m.telephone,
		DATE_FORMAT(audit_time,'%Y-%m-%d %H:%i:%s') auditTime,
		DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') createTime
		from proxybusi p
		left join member m on p.member_id = m.id
          
		  		   and id = #{id} 
		  		   and name = #{name} 
		  		   and status = #{status} 
		  		   and level = #{level} 
		  		   and audit_time = #{auditTime} 
		  		   and create_time = #{createTime} 
		  		   and audit_status = #{auditStatus} 
		  		   and region_level = #{regionLevel} 
		  		   and member_id = #{memberId} 
		  		
        
            
                order by ${sort} ${order}
            
			
                order by id desc
			
        
		
			limit #{offset}, #{limit}

select count(*) from proxybusi
   
