mapper.select:
		select p.`id`,p.`name`,p.`status`,p.`level`,p.`audit_status`,p.`region_level`,p.`user_id`, p.proxy_addr,
		p.`audit_result`,p.`audit_opinion`, p.proxy_telephone, p.proxy_region,
		u.city,u.district,u.`name` memberName,u.province,
		DATE_FORMAT(audit_time,'%Y-%m-%d %H:%i:%s') auditTime,
		DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') createTime
		from proxybusi p
		left join sys_user u on p.user_id = u.user_id

		  		   and p.id = #{id} 
		  		   and p.name = #{name} 
		  		   and p.status = #{status} 
		  		   and p.level = #{level} 
		  		   and p.audit_time = #{auditTime} 
		  		   and p.create_time = #{createTime} 
		  		   and p.audit_status = #{auditStatus} 
		  		   and p.region_level = #{regionLevel} 
		  		   and p.user_id = #{userId} 
		  		   and p.proxy_telephone = #{proxyTelephone} 
		  		   and p.proxy_region = #{proxyRegion} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from proxybusi

  		   and id = #{id} 
  		   and name = #{name} 
  		   and status = #{status} 
  		   and level = #{level} 
  		   and audit_time = #{auditTime} 
  		   and create_time = #{createTime} 
  		   and audit_status = #{auditStatus} 
  		   and region_level = #{regionLevel} 
  		   and user_id = #{userId} 
  		   and proxy_telephone = #{proxyTelephone} 
  		   and proxy_region = #{proxyRegion}

mapper.update:
update proxybusi
`name` = #{name}, `status` = #{status}, `level` = #{level}, `audit_time` = #{auditTime}, `create_time` = #{createTime}, `audit_status` = #{auditStatus}, `region_level` = #{regionLevel}, `user_id` = #{userId}, `audit_result` = #{auditResult}, `audit_opinion` = #{auditOpinion}, `proxy_addr` = #{proxyAddr}, proxy_telephone = #{proxyTelephone}, proxy_region = #{proxyRegion}
where id = #{id}
