mapper.select:
		select t.id,t.team_name teamName,t.colonel_name,colonel_tel,t.user_id,t.team_type,tt.type_name,t.team_code,t.team_title_img,t.team_img,t.reg_code,r.region_name,t.status,t.remark,t.team_introduce,t.addres ,mem.`name` as teamName,t.is_auth,t.auth_ent_time,t.integral
		from team t left join team_type tt on t.team_type = tt.id 
		left join cux_region r on t.reg_code = r.region_code
		left join sys_user mem on mem.user_id=t.user_id

		  		   and t.id = #{id} 
		  		   and t.team_name like concat('%',#{teamName},'%')
		  		   and t.colonel_name = #{colonelName} 
		  		   and t.user_id = #{userId} 
		  		   and t.team_type = #{teamType} 
		  		   and t.team_code = #{teamCode} 
		  		   and t.team_title_img = #{teamTitleImg} 
		  		   and t.team_img = #{teamImg} 
		  		   and t.reg_code = #{regCode} 
		  		    and t.status = #{status} 
		  		   and t.colonel_tel = #{colonelTel}
		  		   and t.is_auth = #{isAuth}
					and t.auth_ent_time = #{authEntTime}

		         	and left(t.id,CHAR_LENGTH(#{ids})) LIKE CONCAT('%',#{ids},'%')  




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*)
from team t left join team_type tt on t.team_type = tt.id 
left join cux_region r on t.reg_code = r.region_code
left join sys_user mem on mem.user_id=t.user_id

  		   and t.id = #{id} 
  		   and t.team_name like concat('%',#{teamName},'%')
  		   and t.colonel_name = #{colonelName} 
  		   and t.user_id = #{userId} 
  		   and t.team_type = #{teamType} 
  		   and t.team_code = #{teamCode} 
  		   and t.team_title_img = #{teamTitleImg} 
  		   and t.team_img = #{teamImg} 
  		   and t.reg_code = #{regCode} 
  		    and t.status = #{status} 
  		   and t.colonel_tel = #{colonelTel}
  		   and t.is_auth = #{isAuth}
			and t.auth_ent_time = #{authEntTime}

         	and left(t.id,CHAR_LENGTH(#{ids})) LIKE CONCAT('%',#{ids},'%')

select t.id,t.team_name,t.colonel_name,t.user_id,t.team_type,tt.type_name,t.team_code,t.team_title_img,t.team_img,t.reg_code,r.region_name,t.status,t.remark,t.team_introduce,t.colonel_tel,t.addres,t.detailed_addr from team t left join team_type tt on t.team_type = tt.id left join 
cux_region r on t.reg_code = r.region_code

	  		   and t.id = #{id} 
	  		   and t.user_id = #{userId} 
	  		    and t.status != #{status}

mapper.update:
update team
`team_name` = #{teamName}, `colonel_name` = #{colonelName}, `user_id` = #{userId}, `team_type` = #{teamType}, `team_code` = #{teamCode}, `team_title_img` = #{teamTitleImg}, `team_img` = #{teamImg}, `reg_code` = #{regCode}, `status` = #{status}, `remark` = #{remark}, `colonel_tel` = #{colonelTel}, `team_introduce` = #{teamIntroduce}, `integral` = #{integral}, `addres` = #{addres}, `detailed_addr` = #{detailedAddr}, is_auth = #{isAuth}, auth_ent_time = #{authEntTime}
where id = #{id}

mapper.select:
		select `id`,`team_name`,`colonel_name`,`user_id`,`team_type`,`team_code`,`team_title_img`,`team_img`,`reg_code`,`team_introduce`,`status`,`remark`,`colonel_tel`,`integral`,`addres`,`detailed_addr`,`is_auth`,`auth_ent_time` from team

		  		   and user_id = #{userId} 
		  		   and team_type = #{teamType} 
		  		   and team_introduce = #{teamIntroduce} 
		  		   and status = #{status} 
		  		   and is_auth = #{isAuth} 
		  		   and auth_ent_time = #{authEntTime} 

		  		   and auth_ent_time <= #{endTime} 




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}
