mapper.select:
		SELECT v.`volunteer_number`,mem.`name` AS volunteer_name,mem.`mobile`,v.`team_id`,mem.`province`,mem.`city`,mem.`district`,
		mem.`region_code`,v.`act_number`,v.`click_number`,v.`comment_number`,v.`create_time`,v.`integral`,
		mem.`sex` FROM volunteer v 
		LEFT JOIN sys_user mem ON mem.`user_id`=v.`user_id`

		  		   and v.id = #{id}      
		  		   and mem.name like CONCAT('%',CONCAT(#{volunteerName},'%')) 
		  		   and v.user_id = #{userId} 
		  		   and v.team_id = #{teamId} 
		  		   and v.address = #{address} 
		  		   and mem.`mobile` = #{telephone} 
		  		   and v.sex = #{sex} 
		  		   and v.age = #{age} 
		  		   and v.volunteer_number = #{volunteerNumber} 
		  		   and v.card_no = #{cardNo} 
		  		   and v.shares_number = #{sharesNumber} 
		  		   and v.comment_number = #{commentNumber} 
		  		   and v.click_number = #{clickNumber} 
		  		   and v.act_number = #{actNumber} 
		  		   and v.level = #{level} 
		  		   and v.province = #{province} 
		  		   and v.city = #{city} 
		  		   and v.county = #{county} 
		  		   and v.regionCode = #{regioncode} 
		  		    and v.create_Time = #{createTime} 
		  		    and v.audit_status = #{auditStatus}
		  		    and introduction <= #{introduction} 
		  		    and v.create_Time >=#{startTime} 
		  		    and create_Time <= #{endTime} 
		  			 AND v.enter_team_time >= #{startEntTeamTime}
				  AND v.enter_team_time <= #{endEntTeamTime}

		  		  	and
		  		  	v.team_id in 

				            #{item}       



		  		  	and
		  		  	v.id in 

				            #{item}       





                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from volunteer

