SELECT u.user_id AS userId, u.head_img AS headImg, u.`name` AS volunteerName, u.province, u.city, t.team_name AS teamName, t.id as teamId, v.act_number as actNumber, v.click_number AS clickNumber, v.comment_number AS commentNumber, v.shares_number AS sharesNumber, v.volunteer_number AS volunteerNumber, v.id FROM volunteer v LEFT JOIN sys_user u ON v.user_id = u.user_id LEFT JOIN team t ON v.team_id = t.id
and v.id = #{id} and v.user_id = #{userId} and v.team_id = #{teamId} and u.is_identification = #{isVo} and u.sex = #{sex}
and v.team_id in
#{item}
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}

SELECT u.user_id AS userId, u.head_img AS headImg, u.`name` AS volunteerName, CONCAT(u.province, u.city) city, u.sex, u.mobile, t.team_name AS teamName, t.id as teamId, v.act_number as actNumber, v.click_number AS clickNumber, v.comment_number AS commentNumber, v.shares_number AS sharesNumber, v.volunteer_number AS volunteerNumber, v.create_time createTime, v.id AS voId FROM volunteer v LEFT JOIN sys_user u ON v.user_id = u.user_id LEFT JOIN team t ON v.team_id = t.id where u.is_identification = 1
and v.create_Time >=#{startTime} and create_Time <= #{endTime} and u.mobile = #{mobile} and u.name like CONCAT('%',CONCAT(#{volunteerName},'%'))
order by ${sort} ${order}
order by v.id desc
limit #{offset}, #{limit}

mapper.update:
update volunteer
`shares_number` = shares_number+1, `comment_number` = comment_number+1, `click_number` = click_number+1, `act_number` = act_number+1, `integral` = integral+#{actNumber}
where user_id = #{id}
