mapper.select:
		select v.id,`volunteer_name`,v.`user_id`, t.team_name teamName,head_img,
		`address`,`telephone`,`sex`,`age`,`volunteer_number`,
		`card_no`,`shares_number`,`comment_number`,`click_number`,
		`act_number`,`level`,`province`,`city`,`county`,`regionCode`,
		audit_status,
		DATE_FORMAT(create_Time,'%Y-%m-%d %H:%i:%s') createTime,v.remark
		from volunteer v
		left join team t on v.team_id = t.id
          
		  		   and id = #{id}      
		  		   and volunteer_name like CONCAT('%',CONCAT(#{volunteerName},'%')) 
		  		   and user_id = #{userId} 
		  		   and team_id = #{teamId} 
		  		   and address = #{address} 
		  		   and telephone = #{telephone} 
		  		   and sex = #{sex} 
		  		   and age = #{age} 
		  		   and volunteer_number = #{volunteerNumber} 
		  		   and card_no = #{cardNo} 
		  		   and shares_number = #{sharesNumber} 
		  		   and comment_number = #{commentNumber} 
		  		   and click_number = #{clickNumber} 
		  		   and act_number = #{actNumber} 
		  		   and level = #{level} 
		  		   and province = #{province} 
		  		   and city = #{city} 
		  		   and county = #{county} 
		  		   and regionCode = #{regioncode} 
		  		    and create_Time = #{createTime} 
		  		    and audit_status = #{auditStatus}
		  		    and create_Time >=#{startTime} 
		  		    and create_Time <= #{endTime} 
		  			 AND enter_team_time >= #{startEntTeamTime}
				  AND enter_team_time <= #{endEntTeamTime}
				  
		  		  	and
		  		  	v.team_id in 
		  		   	
				            #{item}       
				     
		  		  
		  		  
		  		  	and
		  		  	v.id in 
		  		   	
				            #{item}       
				     
		  		  
		  		
        
            
                order by ${sort} ${order}
            
			
                order by id desc
			
        
		
			limit #{offset}, #{limit}

select count(*) from volunteer
   
  		   and id = #{id} 
